# Обратный маппинг для поиска ключа по значению
REVERSE_DEFECT_MAPPING = {v: k for k, v in DEFECT_MAPPING.items()}

def get_defect_full_name(short_key: str) -> str:
    """Получить полное название дефекта по короткому ключу"""
    return DEFECT_MAPPING.get(short_key, short_key)

def get_defect_short_key(full_name: str) -> str:
    """Получить короткий ключ по полному названию дефекта"""
    return REVERSE_DEFECT_MAPPING.get(full_name, full_name)

def get_all_defect_keys() -> list:
    """Получить все доступные короткие ключи"""
    return list(DEFECT_MAPPING.keys())